-- Banco de dados
CREATE DATABASE IF NOT EXISTS Flintstones
  DEFAULT CHARACTER SET utf8mb4
  COLLATE utf8mb4_general_ci;

USE Flintstones;


--  Tabela de arquivos de m√≠dia
CREATE TABLE IF NOT EXISTS media_files (
    id INT AUTO_INCREMENT PRIMARY KEY,
    file_name VARCHAR(255) NOT NULL,
    file_url TEXT NOT NULL,
    file_type ENUM('gif', 'image', 'video', 'audio') NOT NULL,
    category VARCHAR(100) NOT NULL,
    tags VARCHAR(255),
    description TEXT, -- üîπ coluna que faltava
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


--  Tabela de uso da m√≠dia
CREATE TABLE IF NOT EXISTS media_usage (
    id INT AUTO_INCREMENT PRIMARY KEY,
    media_id INT NOT NULL,
    command_name VARCHAR(50) NOT NULL,
    usage_count INT DEFAULT 1,
    last_used TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    FOREIGN KEY (media_id) REFERENCES media_files(id) ON DELETE CASCADE,
    UNIQUE KEY uniq_media_command (media_id, command_name) -- üîë Necess√°rio para ON DUPLICATE KEY
);

-- üë§ Prefer√™ncias do usu√°rio
CREATE TABLE IF NOT EXISTS user_preferences (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id VARCHAR(50) NOT NULL,
    preference_key VARCHAR(50) NOT NULL,
    preference_value VARCHAR(255),
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    UNIQUE KEY uniq_user_pref (user_id, preference_key)
);


 



